HOSTS = ["172.16.17.124", "172.16.17.19"]


class QuickstartUser(HttpUser):
    wait_time = between(1, 2.5)

    def select_headers(self, fields, max=50):
        """Randomly selects between 1 and max fields
        """
        return random.choices(fields, k=random.randint(1, max))

    @task
    def get_AMU_data(self):
        fields = self.select_headers(AMU_FIELDS)
        q = InfluxQueryBuilder("process").range().asset("AMU")
        for f in fields:
            q.field(f)
        self.client.post(f"http://{self.select_headers(HOSTS, max=1)[0]}:8000/api/query", data={"query": q.build()})

    @task
    def get_VISA_data(self):
        fields = self.select_headers(VISA_FIELDS)
        q = InfluxQueryBuilder("process").range().asset("VISA")
        for f in fields:
            q.field(f)
        self.client.post(f"http://{self.select_headers(HOSTS, max=1)[0]}:8000/api/query", data={"query": q.build()})
